% -- Variable Definition -- %h = 6.62607004E-34;Qe=1.602176565E-19;Ev = 6.241509E+18;hbar = (h)/(2*pi);me = 9.1093837015E-31;m=0.043*me;lw = 10E-10:1E-10:200E-10;%% N = 1 %%n=1;numer = hbar*hbar*pi*pi*n*n;denom = 2*m;const = numer/denom;multi = lw.*lw;newarray = 1./multi;finalarray = newarray.*const;converted = finalarray./Qe.*1000;y = sin(n*pi./lw);%% N = 2 %%n = 2;numer1 = hbar*hbar*pi*pi*n*n;denom1 = 2*m;const1 = numer1/denom1;multi1 = lw.*lw;newarray1 = 1./multi1;y1 = sin(n*pi./lw);finalarray1 = newarray1.*const1;converted1 = finalarray1./Qe.*1000;%% N = 3 %%n = 3;numer2 = hbar*hbar*pi*pi*n*n;denom2 = 2*m;const2 = numer2/denom2;multi2 = lw.*lw;newarray2 = 1./multi2;y2 = sin(n*pi./lw);finalarray2 = newarray2.*const2;converted2 = finalarray2./Qe.*1000;n3n2 = converted2 - converted1;%% PLOT %%%subplot(2,1,1)semilogy(lw,converted,lw,converted1,lw,converted2,lw,n3n2)legend({'n = 1','n = 2','n = 3'},'Location','northeast')xlabel('Well Width (Lw)') ylabel('Energy (meV)') %subplot(2,1,2)%plot(lw,y,lw,y1,lw,y2)